db = require('./lib').getClient();

var large = '';

for (var i = 0; i < 100000; i++) {
  large += '*';
}

var results = 0;
for (var i = 0; i < 300; i++) {
  var file = 'file' + i;
  db.save('larger-data', file, large, function() {
    db.get('larger-data', file, function(err, data, meta) {
      results += 1;
      console.log(data.length);
    })
  })
}
setTimeout(function() {
  console.log(results)
}, 5000);
